// SPDX-License-Identifier: MIT
pragma solidity >=0.4.25 <0.9.0;

contract Faucet {

    uint256 public numOfFunders;
    mapping (uint256 => address) public lutFunders;
    mapping (address => bool) public funders;

    receive() external payable {}

    function addFunds() external payable {
        address funder = msg.sender;

        if (!funders[funder]) {
            uint256 index = numOfFunders;
            numOfFunders++;
            funders[funder] = true;
            lutFunders[index] = funder;
        }
    }

    function getFundersIndex(uint256 index) external view returns (address) {
        require(index < numOfFunders, "Invalid index");
        return lutFunders[index];
    }

    function getAllFunders() external view returns (address[] memory) {
        address[] memory _funders = new address[](numOfFunders);

        for (uint256 i = 0; i < numOfFunders; i++) {
            _funders[i] = lutFunders[i];
        }

        return _funders;
    }

   function withdraw(uint256 withdrawAmount) external limitWithdraw(withdrawAmount) {
        require(msg.sender != address(0), "Invalid address");
        require(withdrawAmount <= address(this).balance, "Insufficient balance");

        (bool success, ) = payable(msg.sender).call{value: withdrawAmount}("");
        require(success, "Withdrawal failed");
    }

   modifier limitWithdraw(uint256 withdrawAmount) {
        require(withdrawAmount > 0 && withdrawAmount <= 1 ether, "Invalid withdrawal amount");
        _;
    }
}
